        placardY.invalidateIntrinsicContentSize ()
        let r = NSRect (x: inLocationInView.x, y: inLocationInView.y, width: 10.0, height: 10.0)
        xyPopover.show (relativeTo: r, of: self, preferredEdge: NSRectEdge.minX)
      }
    }else{
      clearXYpopover ()
    }
  }

