func compute_CanariLibraryEntry_mStatusImage (
       _ self_mPath : String
) -> NSImage {
//--- START OF USER ZONE 2
  let fm = FileManager ()
  let possibleImage : NSImage? = fm.fileExists (atPath: self_mPath)
    ? NSImage (named: "green14")
    : NSImage (named: "red14")
  return possibleImage!
