import Cocoa

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func compute_PMArtworkDocument_mStatusImage (_ inArg0 : [ArtworkFileGenerationParameters_fileExtension],
                                             _ inArg1 : [ArtworkFileGenerationParameters_name]) -> NSImage {
//--- START OF USER ZONE 2
  let n = inArg0.count
  var error = false
  let warning = n == 0
  var fileExtensionSet = Set <String> ()
  var nameSet = Set <String> ()
  for i in 0 ..< n {
    let fileExtension = inArg0 [i].fileExtension
    let name = inArg1 [i].name
    if fileExtension == "" {
      error = true
    }else if fileExtensionSet.contains (fileExtension) {
      error = true
    }else{
      fileExtensionSet.insert (fileExtension)
    }
    if name == "" {
      error = true
    }else if nameSet.contains (name) {
      error = true
    }else{
      nameSet.insert (name)
    }
  }
  var result = NSImage (named: "green20")!
  if error {
    result = NSImage (named: "red20")!
  }else if warning {
    result = NSImage (named: "orange20")!
  }
  return result
//--- END OF USER ZONE 2
}

