func compute_PMFontDocument_missingCharactersCountString (_ inArg0 : [FontCharacterEntity_characterIsDefined]) -> String {
//--- START OF USER ZONE 2
  var n = 0
  for c in inArg0 {
    switch c.characterIsDefined_property_selection {
    case .empty, .multiple :
      n += 1
    case .single (let isDefined) :
      if !isDefined {
        n += 1
      }
    }
  }
  let result : String
  if n == 0 {
    result = "All characters are defined"
  }else if n == 1 {
    result = "1 undefined character"
  }else{
    result = "\(n) undefined characters"
  }
  return result
//--- END OF USER ZONE 2
}

