import Cocoa

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func compute_MergerRoot_boardRect (
       _ self_boardInstances_instanceRect : [MergerBoardInstance_instanceRect]
) -> CanariBoardRect {
//--- START OF USER ZONE 2
  var r = CanariBoardRect () // Empty rect
  for board in self_boardInstances_instanceRect {
    switch board.instanceRect {
    case .single (let re) :
      r = r.union (re)
    default :
      break
    }
  }
  return CanariBoardRect (x:0, y:0, width: r.x + r.width, height: r.y + r.height)
//--- END OF USER ZONE 2
}

