 //····················································································································

  @objc func performUndo (_ oldValue : [FontInProject]) {
    self.mValue = oldValue
  }

  //····················································································································

  func remove (_ object : FontInProject) {
    if self.mSet.contains (object) {
      var array = self.mValue
      let idx = array.firstIndex (of: object)
      array.remove (at: idx!)
      self.mValue = array
    }
